#
# Sotution 2: Graph
#
# @param {String[]} deadends
# @param {String} target
# @return {Integer}

def open_lock(deadends, target)
  return -1 if deadends.include?("0000") # Special Condition

  deadends = deadends.to_s
  dp = {"0000" => 0}
  queue = Queue.new 
  root = "0000"
  queue << root  

  while(queue.length > 0) 
    cur_node = queue.shift()
    upNodes = nextNodes(cur_node, :up, dp, deadends, target, queue)
    return upNodes if upNodes.is_a?(Numeric)
    downNodes = nextNodes(cur_node, :down, dp, deadends, target, queue)
    return downNodes if downNodes.is_a?(Numeric)

    # ["1301","0113","0202","0122","3012","0131","0200","0203","1232","0222"]
    # "2311"
  end

  return -1 if dp[target.to_i] == nil

end

def nextNodes(num, direction, dp, deadends, target,queue) 
  digits = num.split("").map {|a| a.to_i}
  nextNodes = []
  if direction == :up
    for i in (0..3) 
      new_digits = digits.dup
      new_digits[i] = (new_digits[i] + 1) % 10
      nextNode = new_digits.join("")
      if dp[nextNode] == nil && !deadends.include?(nextNode)
        dp[nextNode] = dp[num] + 1 
        return dp[nextNode] if nextNode == target
        queue << nextNode
      end 
    end
  elsif direction == :down
    for i in (0..3) 
      new_digits = digits.dup
      new_digits[i] = (new_digits[i] - 1) % 10
      nextNode = new_digits.join("")
      if dp[nextNode] == nil && !deadends.include?(nextNode)
        dp[nextNode] = dp[num] + 1
        return dp[nextNode] if nextNode == target
        queue << nextNode
      end
    end
  end
end   


